import React, { useState } from "react";
import PropTypes from "prop-types";
import Styles from "./styles";

const GameButton = ({ label, buttonClick, positionX, positionY, player }) => {
    const [move, setMove] = useState(false);
    const [buttonFrozen, setButtonFrozen] = useState(false);
    const chooseBorderStyle = () => {
        let style;
        switch (positionX) {
            case 0:
                if(positionY === 1 ) {
                    style = Styles.sideBorder;
                }
                else {
                    style = Styles.noBorder;
                }
                break;
            case 1:
                if(positionY === 1 ) {
                    style = Styles.fullBorder;
                }
                else {
                    style = Styles.horizontalBorder;
                }
                break;
            case 2:
                if(positionY === 1 ) {
                    style = Styles.sideBorder;
                }
                else {
                    style = Styles.noBorder;
                }
                break;
            default:
                style = Styles.noBorder;
                break;
        }
        console.log(style);
        return style
    }

    const handleOnClick = () => {
        if (buttonFrozen) {return}
        setMove(true);
        setButtonFrozen(player);
        buttonClick(positionY)
    }

    return (<div style={chooseBorderStyle()} onClick={!buttonFrozen && handleOnClick()}>{move ? buttonFrozen ? "o" : "x" : label }</div>)
}


GameButton.propTypes = {
    label: PropTypes.string
}

export default GameButton;
