import React, { useState } from "react";
import './App.css';
import GameButton from "./packages/GameButton";

const App = () => {

  const [isCircle, setIsCircle] = useState(true);
  const [moves, setMoves] = useState();

  const createPositions = () => {
    let board = [];
    for (let i = 0; i < 3; i++) {
      for (let j = 0; j < 3; j++) {
      board.push({x: i, y: j})
      }
    }
    return board
  }


  const clickGameButton = data => {
    let takenMoves = moves;
    takenMoves.push(data);
    console.log(takenMoves);
    localStorage.setItem("moves", JSON.stringify(takenMoves));
    setMoves(localStorage.getItem(
        "moves"
    ));
    setIsCircle(!isCircle);
    console.log(data)
  }

  return (
      <div className="App">
        {createPositions().map(singlePosition => (
            <GameButton label={singlePosition.x + "" + singlePosition.y} positionX={singlePosition.x}
                        positionY={singlePosition.y} buttonClick={clickGameButton} player={isCircle} moves={moves}/>))}
      </div>
  );
}

export default App;
