import React, { useState } from "react";
import './App.css';
import GameButton from "./packages/GameButton";

const App = () => {

  const [isCircle, setIsCircle] = useState(true);
  const [aiMoves, setAiMoves] = useState([]);

  const createPositions = () => {
    let board = [];
    for (let i = 0; i < 3; i++) {
      for (let j = 0; j < 3; j++) {
      board.push({x: i, y: j})
      }
    }
    return board
  }


  const clickGameButton = data => {
    if(!isCircle) {
      console.log(aiMoves);
      setAiMoves(aiMoves.push({positionY: data.positionY, positionX: data.positionX}));
    }
    setIsCircle(!isCircle);
    console.log(data)
  }

  return (
      <div className="App">
        {createPositions().map(singlePosition => (
            <GameButton label={singlePosition.x + "" + singlePosition.y} positionX={singlePosition.x}
                        positionY={singlePosition.y} buttonClick={clickGameButton} player={isCircle} moves={aiMoves}/>))}
      </div>
  );
}

export default App;
