const container = {
    width: "50px",
        height: "50px",
        display: "flex",
        justifyContent: "center",
        borderColor: "gray",
        cursor: "pointer"
}

export default {

    circlePlayer: {
        color: "red"
    },
    crossPlayer: {
        color: "green"
    },
    sideBorder: {
        ...container,
        borderLeftWidth: "1px",
        borderRightWidth: "1px"
    },
    horizontalBorder: {
        ...container,
        borderTopWidth: "1px",
        borderBottomWidth: "1px"
    },
    fullBorder: {
        ...container,
        borderWidth: "1px"
    },
    noBorder: {
        ...container,
        border: "none"
    }
}
